import React, { Component } from 'react';
import '../App.css';

class Search extends Component {
    constructor(props) {
        super(props)
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.state = {
            query:'',
            people: [],
        }
    };

    onChange = (e) => {
        e.preventDefault();
        this.setState({
            query: e.target.value
        })
        console.log(this.state.query)
    };

    onSubmit = (e) => {
        e.preventDefault();
        this.search(this.state.query);
    };

    search = query => {
        let res = fetch(`https://swapi.dev/api/people/${this.state.query}`);
        let data = res.json();
        this.setState({
            people: data.results
        })
    };

    render() {
        return (
            <>
                <form onSubmit={this.onSubmit}>
                    <input
                        type="text"
                        onChange={this.onChange}
                    />
                    <button
                        type="submit"
                        className="SearchButton">
                        Search
                    </button>
                </form>
                <div>
                    {this.state.people.map(person => (
                        <ul key={person.name}>
                            <li>{person.name}</li>
                        </ul>
                    ))}
                </div>
            </>
        )
    }
}

export default Search
